using System; using System.Windows.Forms;
namespace SalesInventoryApp { public partial class LoginForm : Form { public LoginForm() { InitializeComponent(); this.Paint += LoginForm_Paint; } private void LoginForm_Paint(object sender, PaintEventArgs e) { using (var brush = new System.Drawing.Drawing2D.LinearGradientBrush(this.ClientRectangle, System.Drawing.Color.FromArgb(102,126,234), System.Drawing.Color.FromArgb(118,75,162), 45F)) { e.Graphics.FillRectangle(brush, this.ClientRectangle); } } private void btnLogin_Click(object sender, EventArgs e) { var username = txtUser.Text.Trim(); var password = txtPass.Text; if (username == "admin" && password == "password") { this.Hide(); var main = new MainForm(); main.FormClosed += (s, ee) => this.Close(); main.Show(); } else { lblError.Text = "Invalid username or password"; } } } }